    tooth_width_base = (PI * m) / 2;

    difference() {
        union() {
            // Gear Hub/Root
            cylinder(r = root_radius + 0.1, h = t);

            // Teeth
            for (i = [0 : n - 1]) {
                rotate([0, 0, i * (360 / n)])
