boss_bore_diameter = 0; // Set > 0 to create a hole through the cylinder

/* [Hole Parameters] */
hole_diameter = 4.5; // M4 clearance
hole_margin = 6;     // Distance from edge to hole center

/* [Rendering] */
$fn = 64;

module mounting_bracket_with_boss() {
    difference() {
        union() {
            // Base plate with rounded corners
            hull() {
                translate([corner_radius, corner_radius, 0])
                    cylinder(r = corner_radius, h = bracket_thickness);
                translate([bracket_length - corner_radius, corner_radius, 0])
                    cylinder(r = corner_radius, h = bracket_thickness);
                translate([bracket_length - corner_radius, bracket_width - corner_radius, 0])
                    cylinder(r = corner_radius, h = bracket_thickness);
                translate([corner_radius, bracket_width - corner_radius, 0])
                    cylinder(r = corner_radius, h = bracket_thickness);
            }

            // Central Cylinder (Boss)
            // Positioned at the exact center of the plate
            translate([bracket_length/2, bracket_width/2, 0])
                cylinder(d = boss_diameter, h = bracket_thickness + boss_height);
        }

        // Four corner clearance holes
        hole_coords = [
            [hole_margin, hole_margin],
            [bracket_length - hole_margin, hole_margin],
            [bracket_length - hole_margin, bracket_width - hole_margin],
            [hole_margin, bracket_width - hole_margin]
        ];

        for (pos = hole_coords) {
            translate([pos[0], pos[1], -0.1])
                cylinder(d = hole_diameter, h = bracket_thickness + 0.2);
        }

        // Optional Bore through the central cylinder
        if (boss_bore_diameter > 0) {
            translate([bracket_length/2, bracket_width/2, -0.1])
                cylinder(d = boss_bore_diameter, h = bracket_thickness + boss_height + 0.2);
        }
    }
}

mounting_bracket_with_boss();
